CREATE TABLE `oborot`.`clientdata` ( `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT , `name` VARCHAR(150) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `surename` VARCHAR(150) VARCHAR CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `phone` BIGINT NOT NULL , `comment` TEXT CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL , `date` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = MyISAM CHARSET=utf8 COLLATE utf8_general_ci;
